import React, { useState, useEffect } from "react";

import { createBrowserRouter, useNavigate } from "react-router-dom";

// import components

import Login from '../pages/Login';
import Register from '../pages/Register';
import Dashboard from '../pages/Dashboard';
import PageNotFound from '../pages/PageNotFound';

// import PrivateRoute from "./ProtectedRoute";

const PrivateRoute = ({ element: Component }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(null);
    const token = sessionStorage.getItem('userTokenId');
    const navigate = useNavigate();

    useEffect(() => {
        const verifyToken = async () => {
            try {
                const response = await fetch('https://fun-cougar-precisely.ngrok-free.app/api/authverify', {
                    method: "GET",
                    headers: {
                        'Authorization': `Bearer ${token}` // Typically, the token should be prefixed with "Bearer "
                    }
                });

                if (response.ok) {
                    const data = await response.json();
                    console.log('Auth TOken',data);
                    setIsAuthenticated(data.isAuthenticated); // Assuming your API returns this field
                } else {
                    setIsAuthenticated(false);
                }
            } catch (error) {
                console.error('Error verifying token:', error);
                setIsAuthenticated(false);
            }
        };

        // if (token) {
        //     verifyToken();
        // } else {
        //     setIsAuthenticated(false);
        // }
    }, [token]);

    // if (isAuthenticated === null) {
    //     // While loading, you can show a spinner or some loading text
    //     return <div>Loading...</div>;
    // }

    // If authenticated, render the component, otherwise redirect to login
    //return isAuthenticated ? <Component /> : navigate('/login');
};

const Router = createBrowserRouter([
    {
        path: '/',
        element: <PrivateRoute element={Dashboard} />,
        index: true
    },
    {
        path: '/login',
        element: <Login />
    },
    {
        path: '/register',
        element: <Register />
    },
    {
        path: '*',
        element: <PageNotFound />
    }
]);

 export default Router;
